<?php

namespace BL\Entity\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * UserRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class NotificationUserRepository extends EntityRepository {

    /**
     * Function to delete user notification
     * @author Masud
     * @copyright Blueliner Marketing
     * @version 0.1
     * @access public
     * @param <int> $notificationId
     * @return String
     */
     function deleteNotificationUser($notificationId){
        $sql = "DELETE FROM BL\Entity\NotificationUser nu WHERE nu.notification_id = ".$notificationId;
        $q = $this->_em->createQuery($sql);
        $q->getResult();
    }

}
