<?php

namespace BL\Entity\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * VendorProductInfoDetailsRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class VendorProductInfoDetailsRepository extends EntityRepository {

    /**
     * Function to get list of product for specifice vendor
     * @author Rashed
     * @copyright Blueliner Marketing
     * @version 0.1
     * @access public
     * @param <int> $vendor_id
     * @return String
     */
     function getVendorProducts($vendor_id){

        $sql = "Select p.id, p.product_name, v.vendor_id  FROM products p INNER JOIN vendor_product_info_details v ON p.id = v.product_id WHERE v.vendor_id = '".$vendor_id."'";
        $con = $this->_em->getConnection();
        $records = $con->fetchAll($sql);
        return $records;


    }
}
